function json(data, status = 200, headers = {}) {
  return new Response(JSON.stringify(data, null, 2), {
    status,
    headers: {
      "Content-Type": "application/json; charset=utf-8",
      "Cache-Control": "no-store",
      ...headers,
    },
  });
}

/**
 * Cloudflare Workers Script Upload API (module):
 * - include a `metadata` part (application/json) with `main_module`
 * - upload the module entrypoint as application/javascript+module
 *
 * IMPORTANT:
 * When you PUT a script via the API, Cloudflare will treat omitted bindings as removed.
 * To prevent self-deploy from wiping Aura Core bindings, we include bindings in metadata.
 *
 * This is the "Option A" fix: hardcode known required bindings for Aura Core.
 * (Later we can upgrade to "Option B": read existing script metadata first and reapply.)
 */
function makeModuleUploadForm(script_name, bundleText, compatibility_date) {
  const fd = new FormData();

  // Hardcoded bindings required by aura-core (staging + prod)
  // - KV namespace: AURA_KV
  // - Service binding: AURA_DEPLOYER (points to this deployer worker)
  const bindings = [
    {
      type: "kv_namespace",
      name: "AURA_KV",
      namespace_id: "9c2ba111589e48c6ba5ad1b924ae8809",
    },
    {
      type: "service",
      name: "AURA_DEPLOYER",
      service: "aura-deployer",
    },
  ];

  const metadataObj = {
    main_module: "index.js",
    compatibility_date: compatibility_date || "2026-01-14",
    bindings,
    // If you later need compatibility flags (e.g. nodejs_compat), add them here:
    // compatibility_flags: ["nodejs_compat"],
  };

  // metadata must be application/json
  const metadataFile = new File([JSON.stringify(metadataObj)], "metadata.json", {
    type: "application/json",
  });
  fd.append("metadata", metadataFile);

  // module file should be application/javascript+module
  const moduleFile = new File([String(bundleText)], "index.js", {
    type: "application/javascript+module",
  });
  fd.append("index.js", moduleFile);

  return fd;
}

function decodeB64ToUtf8(b64) {
  // b64 -> Uint8Array -> string
  const bin = atob(String(b64 || ""));
  const bytes = new Uint8Array(bin.length);
  for (let i = 0; i < bin.length; i++) bytes[i] = bin.charCodeAt(i);
  return new TextDecoder("utf-8").decode(bytes);
}

export default {
  async fetch(request, env) {
    const url = new URL(request.url);

    if (url.pathname === "/health") {
      return json({
        ok: true,
        service: "aura-deployer",
        staging_target: env.STAGING_SCRIPT_NAME,
        prod_target: env.PROD_SCRIPT_NAME,
        has_deploy_key: !!env.DEPLOY_SECRET,
        has_cf_api_token: !!env.CF_API_TOKEN,
        account_id: env.CF_ACCOUNT_ID,
        version: "AURA_DEPLOYER__2026-01-16__PRESERVE_BINDINGS__01__ACCEPT_BUNDLE_B64__01",
      });
    }

    if (url.pathname === "/deploy" && request.method === "POST") {
      const key = request.headers.get("x-deploy-key") || "";
      if (!env.DEPLOY_SECRET || key !== env.DEPLOY_SECRET) {
        return json({ ok: false, error: "unauthorized" }, 401);
      }

      let payload;
      try {
        payload = await request.json();
      } catch {
        return json({ ok: false, error: "invalid_json" }, 400);
      }

      const {
        script_name,
        bundle,
        bundle_b64,
        promote_phrase,
        compatibility_date,
      } = payload || {};

      // Accept either:
      // - bundle: raw module text (original behavior)
      // - bundle_b64: base64 of raw module text (new, matches aura-core forwarder)
      let bundleText = null;
      if (typeof bundle === "string" && bundle.length) {
        bundleText = bundle;
      } else if (typeof bundle_b64 === "string" && bundle_b64.length) {
        try {
          bundleText = decodeB64ToUtf8(bundle_b64);
        } catch {
          return json({ ok: false, error: "bundle_base64_decode_failed" }, 400);
        }
      }

      if (!script_name || !bundleText) {
        return json({ ok: false, error: "missing_required_fields" }, 400);
      }

      if (
        script_name === env.PROD_SCRIPT_NAME &&
        promote_phrase !== env.PROMOTE_PHRASE
      ) {
        return json({ ok: false, error: "promotion_phrase_required" }, 403);
      }

      const form = makeModuleUploadForm(script_name, bundleText, compatibility_date);

      const upload = await fetch(
        `https://api.cloudflare.com/client/v4/accounts/${env.CF_ACCOUNT_ID}/workers/scripts/${script_name}`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${env.CF_API_TOKEN}`,
            // Do NOT set Content-Type; fetch sets boundary for multipart.
          },
          body: form,
        }
      );

      let result;
      try {
        result = await upload.json();
      } catch {
        result = null;
      }

      if (!upload.ok || !result || result.success !== true) {
        return json(
          {
            ok: false,
            error: "cloudflare_upload_failed",
            status: upload.status,
            detail: result,
          },
          500
        );
      }

      return json({ ok: true, deployed: script_name });
    }

    return json({ ok: false, error: "not_found" }, 404);
  },
};
